// Extract form data and prepare for scraping
const formData = $json;

// Check if formData exists
if (!formData) {
  throw new Error('Form data is missing');
}

// Parse budget range with safety checks
let budgetMatch = null;
let minBudget = 0;
let maxBudget = 999999;

if (formData.Budget_Range) {
  budgetMatch = formData.Budget_Range.match(/(\d+).*?(\d+)/);
  if (budgetMatch) {
    minBudget = parseInt(budgetMatch[1]);
    maxBudget = parseInt(budgetMatch[2]);
  }
}

// System Prompt for Jina.ai
const systemPrompt = `You are a specialized web scraping agent for Sabari Musicals.

Extract from https://sabarimusicals.com/product-category/musical-instruments/:
1. Product names, prices, descriptions, links, images
2. Shop contact details (phone, email, address)
3. Focus on ${formData.Product_type} products
4. Filter by budget: ‚Çπ${minBudget} - ‚Çπ${maxBudget}

Return structured JSON with products array and shopDetails object.`;

// User Prompt for Jina.ai
const userPrompt = `Scrape https://sabarimusicals.com/product-category/musical-instruments/

Customer: ${formData.Name || 'Unknown'}
Product: ${formData.Product_type || 'Guitar'}
Budget: ${formData.Budget_Range || 'Not specified'}
Requirement: ${formData.Message || 'No specific requirements'}

Task:
1. Extract ALL ${formData.Product_type || 'Guitar'} products
2. Filter within ‚Çπ${minBudget.toLocaleString()} - ‚Çπ${maxBudget.toLocaleString()}
3. Prioritize beginner-friendly options
4. Get shop contact details
5. Return top 5 best matches

Focus on: ${formData.Message || 'beginner-friendly instruments'}`;

return {
  json: {
    formData: formData,
    systemPrompt: systemPrompt,
    userPrompt: userPrompt,
    scrapingUrl: 'https://sabarimusicals.com/product-category/musical-instruments/',
    minBudget: minBudget,
    maxBudget: maxBudget,
    productType: formData.Product_type || 'Guitar',
    customerName: formData.Name || 'Customer',
    customerEmail: formData.Email || ''
  }
};

// Process scraped data from Jina.ai
const scrapedContent = $json.body || $json.text || $json;
const formData = $node['Prepare Scraping Prompts'].json.formData;
const minBudget = $node['Prepare Scraping Prompts'].json.minBudget;
const maxBudget = $node['Prepare Scraping Prompts'].json.maxBudget;

// Parse scraped content (this would contain HTML/text from Jina.ai)
// You'll need to extract products using regex or parsing
let products = [];

// Example parsing logic - adjust based on actual Jina.ai response
try {
  // If Jina returns structured data
  if (typeof scrapedContent === 'object' && scrapedContent.products) {
    products = scrapedContent.products;
  } else {
    // Parse text content for product information
    const productMatches = scrapedContent.matchAll(/Product:(.*?)Price:\s*‚Çπ([\d,]+)/gs);
    for (const match of productMatches) {
      const price = parseInt(match[2].replace(/,/g, ''));
      if (price >= minBudget && price <= maxBudget) {
        products.push({
          name: match[1].trim(),
          price: price,
          priceText: `‚Çπ${price.toLocaleString('en-IN')}`
        });
      }
    }
  }
} catch (e) {
  console.error('Error parsing scraped data:', e);
}

// Default shop details if not scraped
const shopDetails = scrapedContent.shopDetails || {
  name: 'Sabari Musicals',
  address: 'Chennai, Tamil Nadu',
  phone: '044-XXXXXXXX',
  email: 'info@sabarimusicals.com',
  website: 'https://sabarimusicals.com',
  experience: '15+ years of trusted service in Chennai'
};

// Filter and limit to top 5 products
const filteredProducts = products
  .filter(p => p.price >= minBudget && p.price <= maxBudget)
  .slice(0, 5);

return {
  json: {
    formData: formData,
    products: filteredProducts,
    shopDetails: shopDetails,
    scrapedRaw: scrapedContent
  }
};

